var svg_1=document.getElementById("svg_1"),svg_2=document.getElementById("svg_2"),svg_3=document.getElementById("svg_3"),svg_4=document.getElementById("svg_4"),w=window.innerWidth,h=window.innerHeight;window.addEventListener("mousemove",function(t){var e=t.clientX,n=t.clientY;svg_1.style.fill="rgb(0,"+Math.round(e/w*100)+","+Math.round(n/h*100)+")",svg_2.style.fill="rgb(255,"+Math.round(255-n/h*100)+","+Math.round(255-e/w*100)+")",svg_3.style.fill="rgb(0,"+Math.round(n/h*100)+","+Math.round(e/w*100)+")",svg_4.style.fill="rgb(255,"+Math.round(255-e/w*100)+","+Math.round(255-n/h*100)+")"});